# room-height
# room-start

: dude 1 sprite-id + @ ;
: door 2 sprite-id + @ ;

: go-start
	' main-starting-room
	' load-starting-room
	load-map
;

:data yomsg 2
:string $ "Hey, dude."
:string $ "What's up?"

: yo
	yomsg show-text
;

: load-big-room

	clear-actors
	3 3 room >room

	16x32  0 152 104 true player >actor
	16x32 24 480 592 true dude   >actor
	16x32 29 720 320 true door   >actor

	' go-start door trigger!
	' yo       dude trigger!
;

: main-big-room
		
	loop

		use-logic
		use-prompt
		move-player
		sort-sprites
		scroll-room

		sync
	again
;